CREATE TABLE Country_info(id int, country_name varchar(30), Capitals varchar (20),Currency varchar(20),Total_population bigint , Nationalgame varchar(20),NationalAnimal varchar(20),Nationalflower varchar(20),NationalAnthem varchar(20));
Select*From Country_info;
delete from Country_info where id=16;
CREATE TABLE Country_details AS SELECT id,country_name,Capitals,NationalAnthem FROM Country_info;
Select*From country_details;
/*AGGRIGATE FUNCTION*/
SELECT count(*) as no_of_rows from country_details;
SELECT SUM(total_population) as total from Country_info where id < 10;
SELECT max(total_population) from Country_info;
SELECT min(total_population) from Country_info;
SELECT AVG(total_population) as avg_population from Country_info;
SELECT COUNT(Capitals), country_name FROM country_info GROUP BY country_name HAVING COUNT(id) < 10;
SELECT SUM(Total_population),country_name From country_info GROUP BY country_name HAVING COUNT(id)<10;